class AchievementBlock{constructor(parentDOM,data){this.parentDOM=parentDOM,this.data=data,this.init()}init(){this.render()}render(){let numberHTML="";this.data.number&&(numberHTML=`<div class="number">${this.data.number}</div>`);let descriptionHTML="";this.data.description&&(descriptionHTML=`<div class="description">${this.data.description}</div>`);const HTML=`\n            <div class="col-3 col-md-6 col-xs-12">\n                <i class="fa fa-${this.data.icon}"></i>\n                ${numberHTML}\n                <h4>${this.data.title}</h4>\n                ${descriptionHTML}\n            </div>`;this.parentDOM.insertAdjacentHTML("beforeend",HTML)}}export default AchievementBlock;