class JobBlock{constructor(parentDOM,data){this.parentDOM=parentDOM,this.DOM=null,this.data=data,this.init()}init(){this.render()}render(){let location="";const{city:city,country:country}=this.data.location;city&&(location+=city),country&&(city&&(location+=", "),location+=country);const HTML=`\n            <div class="job-item">\n                <div class="header">\n                    <div class="texts">\n                        <h3>${this.data.jobTitle}</h3>\n                        <p>${location}</p>\n                    </div>\n                    <div class="btn">${this.formatDate(this.data.duration)}</div>\n                </div>\n                <p>${this.data.description}</p>\n            </div>`;this.parentDOM.insertAdjacentHTML("beforeend",HTML)}formatDate(date){const{from:from,untill:untill}=date;let time="";return time+=from&&"object"==typeof from?this.dateObjectToMonthYear(from):"Birth",time+=" to ",time+=untill&&"object"==typeof untill?this.dateObjectToMonthYear(untill):"Present",time}dateObjectToMonthYear(obj){const{month:month,year:year}=obj,shortYear=year%100,monthNames=["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"];return monthNames[month]+"'"+(shortYear>9?shortYear:"0"+shortYear)}}export default JobBlock;